mapper.delete:
delete from CMS_LOG
where ID = #{id,jdbcType=VARCHAR}
AND　IS_DELETED='N'

mapper.select:
select
from CMS_LOG T LEFT JOIN ipanther_user AS u ON u.id = T.CREATOR LEFT JOIN ipanther_user_dept AS ut ON ut.USER_ID = u.ID where T.IS_DELETED='N'
AND #{pararmMap.searchUserId, jdbcType=VARCHAR}
AND ut.DEPT_ID IN
#{deptId,jdbcType=VARCHAR}
GROUP BY T.ID ORDER BY T.CREATE_TIME,T.ID DESC
